import React from "react";
import { useParams } from "react-router-dom";
import Button from "../../shared/components/FormElements/Button";
import Input from "../../shared/components/FormElements/Input";
import {
  VALIDATOR_REQUIRE,
  VALIDATOR_MINLENGTH,
} from "../../shared/components/util/validators";
const DUMMY_PLACES = [
  {
    id: "p1",
    title: "Empire State Building",
    description: "one of the highest buildings",
    imageUrl:
      "https://archello.s3.eu-central-1.amazonaws.com/images/2018/06/29/Burj-Khalifa.1530286094.553.jpg",
    address:
      "1 Sheikh Mohammed bin Rashid Blvd - Downtown Dubai - Dubai - United Arab Emirates",
    location: {
      lat: 25.197197,
      lng: 55.2721877,
    },
    creator: "u1",
  },
  {
    id: "p2",
    title: "Empire State Building",
    description: "one of the highest buildings",
    imageUrl:
      "https://archello.s3.eu-central-1.amazonaws.com/images/2018/06/29/Burj-Khalifa.1530286094.553.jpg",
    address:
      "1 Sheikh Mohammed bin Rashid Blvd - Downtown Dubai - Dubai - United Arab Emirates",
    location: {
      lat: 25.197197,
      lng: 55.2721877,
    },
    creator: "u2",
  },
];

export default function UpdatePlace() {
  const placeId = useParams().placeId;
  const identifiedPlace = DUMMY_PLACES.find((p) => p.id === placeId);

  if (!identifiedPlace) {
    return <h2 className="center">couldn't find it</h2>;
  }

  return (
    <form>
      <Input
        id="title"
        element="input"
        type="text"
        label="Title"
        validator={[VALIDATOR_REQUIRE()]}
        errorText="Please enter a valid title."
        onInput={() => {}}
        value={identifiedPlace.title}
        valid={true}
      />
      <Input
        id="description"
        element="textarea"
        type="text"
        label="Description"
        validator={[VALIDATOR_MINLENGTH(5)]}
        errorText="Please enter a valid description - min 5 characters."
        onInput={() => {}}
        value={identifiedPlace.title}
        valid={true}
      />
      <Button type='submit' dis></Button>
    </form>
  );
}
