import React from "react";

import './NewGoal.css'

const NewGoal = props => {

    const addGoalHandler = e => {

        e.preventDefault(); 
        const newGoal = {
            id: Math.random().toString(), 
            text: "my new goal"
        }
        props.onAddGoal(newGoal); 
    }

  return (
    <form className="new-goal" onSubmit={addGoalHandler} >
      <input type="text" />
      <button type="submit">Add Goal</button>
    </form>
  );
};

export default NewGoal;
